import React, { Component } from "react";
import { Container, Form, Input, Label, FormGroup, Button } from "reactstrap";
import { register } from "../../actions/authActions";
import { connect } from "react-redux";
import style from "./Login.module.css";

class Login extends Component {
  state = {
    name: "",
    email: "",
    password: "",
    signup: false,
  };

  onChange = (e) => {
    const stateName = e.target.name;
    const stateValue = e.target.value;
    this.setState({ [stateName]: stateValue });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const { name, email, password } = this.state;
    const obj = {
      name,
      email,
      password,
    };
    console.log(obj);
    this.props.register(obj);
  };

  render() {
    // console.log(this.props);
    return (
      <div className={style.mainContainer}>
        <div className={style.loginContainer}>
          <Container style={{ margin: "10px 0" }}>
            <Form>
              <FormGroup>
                <Label>Name</Label>
                <Input
                  type="text"
                  name="name"
                  placeholder="name"
                  onChange={this.onChange}
                />
              </FormGroup>
              <FormGroup>
                <Label>Email</Label>
                <Input
                  type="email"
                  name="email"
                  placeholder="Email"
                  onChange={this.onChange}
                />
              </FormGroup>
              <FormGroup>
                <Label>Password</Label>
                <Input
                  type="password"
                  name="password"
                  placeholder="Password"
                  onChange={this.onChange}
                />
              </FormGroup>
              <FormGroup>
                <Button onClick={this.setState({ signup: true })}>
                  Need a new account ? Sign up!
                </Button>
              </FormGroup>
              <FormGroup>
                <Button block type="submit" onClick={this.onSubmit}>
                  Register
                </Button>
              </FormGroup>
            </Form>
          </Container>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
  user: state.auth.user,
});

export default connect(mapStateToProps, { register })(Login);
