import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  USER_LOADED,
} from "./types";
import axios from "axios";

export const loadUser = () => (dispatch, getState) => {
  axios.get("/api/users/").then((res) => {
    console.log(res.data);
    dispatch({
      type: USER_LOADED,
      payload: res.data,
    });
  });
};

export const register = (toSendObj) => (dispatch) => {
  axios
    .post("/api/users/register/", toSendObj)
    .then((res) => {
      console.log(res.data);
      dispatch({
        type: REGISTER_SUCCESS,
        payload: res.data,
      });
    })
    .catch((err) =>
      dispatch({
        type: REGISTER_FAIL,
      })
    );
};

export const login = (toSendObj) => (dispatch) => {
  axios
    .post("/api/auth/", toSendObj)
    .then((res) => {
      console.log(res.data);
      dispatch({
        type: LOGIN_SUCCESS,
        payload: res.data,
      });
    })
    .catch((err) =>
      dispatch({
        type: LOGIN_FAIL,
      })
    );
};
