import React from "react";
import { Container, Card, CardHeader, CardBody, CardFooter } from "reactstrap";
import style from "./Post.module.css";

const Post = ({ title, description, posted_at, comments }) => {
  return (
    <div className={style.postContainer}>
      <Container>
        <Card body style={{ border: "1px solid grey", margin: "20px 0" }}>
          <CardBody>{title}</CardBody>
          <CardBody>{description}</CardBody>
          <CardBody className="text-muted">{posted_at}</CardBody>
          {comments.map((comment) => {
            return <CardFooter>{comment.comment}</CardFooter>;
          })}
        </Card>
      </Container>
    </div>
  );
};

export default Post;
