import { POSTS_LOADED } from "../actions/types";

const initialState = {
  posts: null,
  isLoading: false,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case POSTS_LOADED:
      return {
        ...state,
        posts: action.payload,
      };
  }
}
